class SampleManager:
    def get_samples_refs(self, langs=None, sizes=None):
        langs = langs or list(SAMPLES)
        sizes = sizes or list(set([
            size
            for lang in langs
            for size in SAMPLES[lang]
        ]))
        refs = [
            (lang, size, idx)
            for lang in langs
            for size in sizes
            for idx in range(len(SAMPLES[lang][size]))
        ]
        return refs

    def get_sample(self, lang, size, idx):
        return SAMPLES[lang][size][idx]
